using System.Collections.Generic;
using System.Text.RegularExpressions;

//email and url regular expressions are by Scott Gonzalez: http://projects.scottsplayground.com/email_address_validation/

namespace Metsys.Validate.Validators
{ 
    internal sealed class CannedPatternValidator : IValidator, IDoJavascript
    {
        private static readonly IDictionary<ValidationPattern, KeyValuePair<string, string>> _jsonLookup = new Dictionary<ValidationPattern, KeyValuePair<string, string>>
                                                                                {
                                                                                    {ValidationPattern.Email, new KeyValuePair<string, string>("email", "true")},
                                                                                    {ValidationPattern.Digits, new KeyValuePair<string, string>("digits", "true")},
                                                                                    {ValidationPattern.Number, new KeyValuePair<string, string>("number", "true")},
                                                                                    {ValidationPattern.CreditCard, new KeyValuePair<string, string>("creditcard", "true")},
                                                                                    {ValidationPattern.Url, new KeyValuePair<string, string>("url", "true")},
                                                                                };

        private static readonly IDictionary<ValidationPattern, Regex> _regexLookup = new Dictionary<ValidationPattern, Regex>
                                                                                {
                                                                                    {ValidationPattern.Email, new Regex(@"^((([a-z]|\d|[!#\$%&'\*\+\-\/=\?\^_`{\|}~]|[\u00A0-\uD7FF\uF900-\uFDCF\uFDF0-\uFFEF])+(\.([a-z]|\d|[!#\$%&'\*\+\-\/=\?\^_`{\|}~]|[\u00A0-\uD7FF\uF900-\uFDCF\uFDF0-\uFFEF])+)*)|((\x22)((((\x20|\x09)*(\x0d\x0a))?(\x20|\x09)+)?(([\x01-\x08\x0b\x0c\x0e-\x1f\x7f]|\x21|[\x23-\x5b]|[\x5d-\x7e]|[\u00A0-\uD7FF\uF900-\uFDCF\uFDF0-\uFFEF])|(\\([\x01-\x09\x0b\x0c\x0d-\x7f]|[\u00A0-\uD7FF\uF900-\uFDCF\uFDF0-\uFFEF]))))*(((\x20|\x09)*(\x0d\x0a))?(\x20|\x09)+)?(\x22)))@((([a-z]|\d|[\u00A0-\uD7FF\uF900-\uFDCF\uFDF0-\uFFEF])|(([a-z]|\d|[\u00A0-\uD7FF\uF900-\uFDCF\uFDF0-\uFFEF])([a-z]|\d|-|\.|_|~|[\u00A0-\uD7FF\uF900-\uFDCF\uFDF0-\uFFEF])*([a-z]|\d|[\u00A0-\uD7FF\uF900-\uFDCF\uFDF0-\uFFEF])))\.)+(([a-z]|[\u00A0-\uD7FF\uF900-\uFDCF\uFDF0-\uFFEF])|(([a-z]|[\u00A0-\uD7FF\uF900-\uFDCF\uFDF0-\uFFEF])([a-z]|\d|-|\.|_|~|[\u00A0-\uD7FF\uF900-\uFDCF\uFDF0-\uFFEF])*([a-z]|[\u00A0-\uD7FF\uF900-\uFDCF\uFDF0-\uFFEF])))\.?$", RegexOptions.Compiled | RegexOptions.IgnoreCase)},
                                                                                    {ValidationPattern.Digits, new Regex(@"^\d+$", RegexOptions.Compiled | RegexOptions.IgnoreCase)},
                                                                                    {ValidationPattern.Number, new Regex(@"^-?(?:\d+|\d{1,3}(?:,\d{3})+)(?:\.\d+)?$", RegexOptions.Compiled | RegexOptions.IgnoreCase)},
                                                                                    {ValidationPattern.Url, new Regex(@"^(https?|ftp):\/\/(((([a-z]|\d|-|\.|_|~|[\u00A0-\uD7FF\uF900-\uFDCF\uFDF0-\uFFEF])|(%[\da-f]{2})|[!\$&'\(\)\*\+,;=]|:)*@)?(((\d|[1-9]\d|1\d\d|2[0-4]\d|25[0-5])\.(\d|[1-9]\d|1\d\d|2[0-4]\d|25[0-5])\.(\d|[1-9]\d|1\d\d|2[0-4]\d|25[0-5])\.(\d|[1-9]\d|1\d\d|2[0-4]\d|25[0-5]))|((([a-z]|\d|[\u00A0-\uD7FF\uF900-\uFDCF\uFDF0-\uFFEF])|(([a-z]|\d|[\u00A0-\uD7FF\uF900-\uFDCF\uFDF0-\uFFEF])([a-z]|\d|-|\.|_|~|[\u00A0-\uD7FF\uF900-\uFDCF\uFDF0-\uFFEF])*([a-z]|\d|[\u00A0-\uD7FF\uF900-\uFDCF\uFDF0-\uFFEF])))\.)+(([a-z]|[\u00A0-\uD7FF\uF900-\uFDCF\uFDF0-\uFFEF])|(([a-z]|[\u00A0-\uD7FF\uF900-\uFDCF\uFDF0-\uFFEF])([a-z]|\d|-|\.|_|~|[\u00A0-\uD7FF\uF900-\uFDCF\uFDF0-\uFFEF])*([a-z]|[\u00A0-\uD7FF\uF900-\uFDCF\uFDF0-\uFFEF])))\.?)(:\d*)?)(\/((([a-z]|\d|-|\.|_|~|[\u00A0-\uD7FF\uF900-\uFDCF\uFDF0-\uFFEF])|(%[\da-f]{2})|[!\$&'\(\)\*\+,;=]|:|@)+(\/(([a-z]|\d|-|\.|_|~|[\u00A0-\uD7FF\uF900-\uFDCF\uFDF0-\uFFEF])|(%[\da-f]{2})|[!\$&'\(\)\*\+,;=]|:|@)*)*)?)?(\?((([a-z]|\d|-|\.|_|~|[\u00A0-\uD7FF\uF900-\uFDCF\uFDF0-\uFFEF])|(%[\da-f]{2})|[!\$&'\(\)\*\+,;=]|:|@)|[\uE000-\uF8FF]|\/|\?)*)?(\#((([a-z]|\d|-|\.|_|~|[\u00A0-\uD7FF\uF900-\uFDCF\uFDF0-\uFFEF])|(%[\da-f]{2})|[!\$&'\(\)\*\+,;=]|:|@)|\/|\?)*)?$", RegexOptions.Compiled | RegexOptions.IgnoreCase)},
                                                                                };
    
        private readonly ValidationPattern _pattern;
        
        public CannedPatternValidator(ValidationPattern pattern)
        {
            _pattern = pattern;
        }

        public IEnumerable<KeyValuePair<string, string>> ToJson()
        {
            yield return _jsonLookup[_pattern];
        }

        public bool IsValid(object value, object container)
        {
            if (value == null) { return true; }
            var @string = value.ToString();
            if (@string.Length == 0) { return true; }
            return _pattern == ValidationPattern.CreditCard ? IsValidCreditCard(@string) : _regexLookup[_pattern].IsMatch(@string);
        }

        private static bool IsValidCreditCard(string @string)
        {
            return true; //todo 
        }


    }
}